import React, { Component } from 'react';
import './App.css';
import RegistrationForm from './RegistrationForm';

import Backendless from 'backendless';
import LoginForm from "./LoginForm";
import ForgotPasswordForm from "./ForgotPasswordForm";

const APP_ID = process.env.REACT_APP_BACKENDLESS_APP_ID;
const API_KEY = process.env.REACT_APP_BACKENDLESS_SECRET_KEY;

Backendless.serverURL = 'https://eu-api.backendless.com';
Backendless.initApp(APP_ID, API_KEY);

const initialState = {
    loading: true,
    message: '',
    error  : null
}

class App extends Component {
    state = initialState

    render() {
        const { error, loading, message } = this.state

        return (
            <div className="App">
                {/*<div className="App-header">*/}

                {/*</div>*/}
                <RegistrationForm/>
                <ForgotPasswordForm/>
                <LoginForm error={error} loading={loading} message={message} />
            </div>
        );
    }
}

export default App;
