import React, { useState } from "react";
import Backendless from "backendless";

const UserFileManager = () => {
    const [folderName, setFolderName] = useState("");
    const [error, setError] = useState("");
    const [files, setFiles] = useState([]);

    const handleCreateFolder = async (e) => {
        e.preventDefault();
        const user = Backendless.UserService.currentUser;
        try {
            await Backendless.Files.createDirectory(`/user_files/${user.name}/${folderName}`);
            alert(`Папка ${folderName} успішно створена!`);
            setFolderName("");
            setError("");
        } catch (error) {
            console.error('Error creating folder:', error);
            setError('Помилка під час створення папки');
        }
    };

    const handleDeleteFolder = async (folderName) => {
        const user = Backendless.UserService.currentUser;
        try {
            await Backendless.Files.removeDirectory(`/user_files/${user.name}/${folderName}`);
            alert(`Папка ${folderName} успішно видалена!`);
        } catch (error) {
            console.error('Error deleting folder:', error);
            setError('Помилка під час видалення папки');
        }
    };

    const handleViewServerFiles = async () => {
        const user = Backendless.UserService.currentUser;
        try {
            const folderContents = await Backendless.Files.listing(`/user_files/${user.name}`);
            setFiles(folderContents);
        } catch (error) {
            console.error('Error viewing server files:', error);
            setError('Помилка під час перегляду файлів на сервері');
        }
    };

    const handleViewLocalFiles = async () => {
        try {
            // Реалізація перегляду файлів на локальному пристрої
            alert("Реалізація перегляду файлів на локальному пристрої");
        } catch (error) {
            console.error('Error viewing local files:', error);
            setError('Помилка під час перегляду файлів на пристрої');
        }
    };

    const handleUploadFileToServer = async () => {
        try {
            // Реалізація завантаження файлу на сервер
            alert("Реалізація завантаження файлу на сервер");
        } catch (error) {
            console.error('Error uploading file to server:', error);
            setError('Помилка під час завантаження файлу на сервер');
        }
    };

    const handleDownloadFileFromServer = async () => {
        try {
            // Реалізація завантаження файлу з сервера на пристрій
            alert("Реалізація завантаження файлу з сервера на пристрій");
        } catch (error) {
            console.error('Error downloading file from server:', error);
            setError('Помилка під час завантаження файлу з сервера');
        }
    };

    const handleDeleteFileFromServer = async (fileName) => {
        const user = Backendless.UserService.currentUser;
        try {
            await Backendless.Files.remove(`/user_files/${user.name}/${fileName}`);
            alert(`Файл ${fileName} успішно видалено з сервера!`);
        } catch (error) {
            console.error('Error deleting file from server:', error);
            setError('Помилка під час видалення файлу з сервера');
        }
    };

    return (
        <div>
            <h2>Управління файлами користувача</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleCreateFolder}>
                <div>
                    <label>Ім'я папки:</label>
                    <input type="text" value={folderName} onChange={(e) => setFolderName(e.target.value)} required />
                </div>
                <button type="submit">Створити папку</button>
            </form>

            <h2>Видалення файлу / папки</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>Ім'я файлу / папки:</label>
                <input type="text" value={folderName} onChange={(e) => setFolderName(e.target.value)} required />
                <button onClick={() => handleDeleteFolder(folderName)}>Видалити</button>
            </div>

            <h2>Перегляд файлів на сервері</h2>
            <button onClick={handleViewServerFiles}>Переглянути</button>

            <h2>Перегляд файлів на пристрої</h2>
            <button onClick={handleViewLocalFiles}>Переглянути</button>

            <h2>Завантаження файлу на сервер</h2>
            <button onClick={handleUploadFileToServer}>Завантажити</button>

            <h2>Завантаження файлу з сервера на пристрій</h2>
            <button onClick={handleDownloadFileFromServer}>Завантажити</button>

            <h2>Видал ення файлу на сервері</h2>
            <div>
                <label>Ім'я файлу:</label>
                <input type="text" value={folderName} onChange={(e) => setFolderName(e.target.value)} required />
                <button onClick={() => handleDeleteFileFromServer(folderName)}>Видалити</button>
            </div>
        </div>
    );
};

export default UserFileManager;
