import React, { Component } from 'react';
import Backendless from 'backendless';

class UserFileManager extends Component {
    state = {
        fileName: '',
        error: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleUpload = async (e) => {
        e.preventDefault();

        const { fileName } = this.state;

        try {
            // Отримати робочий каталог користувача
            const userDirectory = `/user_files/${Backendless.UserService.getCurrentUser().name}`;

            // Викликати метод Backendless для завантаження файлу в робочий каталог користувача
            // У цьому прикладі ми просто створюємо файл з ім'ям, введеним користувачем
            const fileUrl = await Backendless.Files.uploadFile(userDirectory, fileName, null, true);

            // Успішне повідомлення про завантаження файлу
            alert(`Файл ${fileName} успішно завантажено!`);
        } catch (error) {
            // Помилка під час завантаження файлу
            console.error('Error uploading file:', error);
            this.setState({ error: 'Помилка під час завантаження файлу' });
        }
    };

    render() {
        const { fileName, error } = this.state;

        return (
            <div>
                <h2>Управління файлами користувача</h2>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <form onSubmit={this.handleUpload}>
                    <div>
                        <label>Ім'я файлу:</label>
                        <input type="text" name="fileName" value={fileName} onChange={this.handleChange} required />
                    </div>
                    <button type="submit">Завантажити файл</button>
                </form>
            </div>
        );
    }
}

export default UserFileManager;
