import React, { Component } from 'react';
import Backendless from 'backendless';

class UserFileManager extends Component {
    state = {
        folderName: '',
        error: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleCreateFolder = async (e) => {
        const user = Backendless.UserService.currentUser;

        e.preventDefault();

        const { folderName } = this.state;

        try {
            // Отримати робочий каталог користувача
            const userDirectory = `/user_files/${user.name}`;

            // Викликати метод Backendless для створення папки у робочому каталозі користувача
            await Backendless.Files.createDirectory(`${userDirectory}/${folderName}`);

            // Успішне повідомлення про створення папки
            alert(`Папка ${folderName} успішно створена!`);
        } catch (error) {
            // Помилка під час створення папки
            console.error('Error creating folder:', error);
            this.setState({ error: 'Помилка під час створення папки' });
        }
    };

    render() {
        const { folderName, error } = this.state;

        return (
            <div>
                <h2>Управління файлами користувача</h2>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <form onSubmit={this.handleCreateFolder}>
                    <div>
                        <label>Ім'я папки:</label>
                        <input type="text" name="folderName" value={folderName} onChange={this.handleChange} required />
                    </div>
                    <button type="submit">Створити папку</button>
                </form>
            </div>
        );
    }
}

export default UserFileManager;
