import React, { useState } from "react";
import Backendless from "backendless";

function UserFileManager() {
    const [createFolderName, setCreateFolderName] = useState("");
    const [deleteFolderName, setDeleteFolderName] = useState("");
    const [createFolderError, setCreateFolderError] = useState("");
    const [deleteFolderError, setDeleteFolderError] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === "createFolderName") {
            setCreateFolderName(value);
        } else {
            setDeleteFolderName(value);
        }
    };

    const handleCreateFolder = async (e) => {
        e.preventDefault();

        const user = Backendless.UserService.currentUser;
        try {
            await Backendless.Files.createDirectory(`/user_files/${user.name}/${deleteFolderName}`);
            alert(`Папка ${createFolderName} успішно створена!`);
            setCreateFolderName("");
            setCreateFolderError("");
        } catch (error) {
            console.error('Error creating folder:', error);
            setCreateFolderError('Помилка під час створення папки');
        }
    };

    const handleDeleteFolder = async (e) => {
        e.preventDefault();
        const user = Backendless.UserService.currentUser;
        try {
            await Backendless.Files.removeDirectory(`/user_files/${user.name}/${deleteFolderName}`);
            alert(`Папка ${deleteFolderName} успішно видалена!`);
            setDeleteFolderName("");
            setDeleteFolderError("");
        } catch (error) {
            console.error('Error deleting folder:', error);
            setDeleteFolderError('Помилка під час видалення папки');
        }
    };

    return (
        <div>
            <div>
                <h2>Створення папки</h2>
                {createFolderError && <p style={{ color: 'red' }}>{createFolderError}</p>}
                <form onSubmit={handleCreateFolder}>
                    <div>
                        <label>Ім'я папки:</label>
                        <input type="text" name="createFolderName" value={createFolderName} onChange={handleChange} required />
                    </div>
                    <button type="submit">Створити папку</button>
                </form>
            </div>

            <div>
                <h2>Видалення папки</h2>
                {deleteFolderError && <p style={{ color: 'red' }}>{deleteFolderError}</p>}
                <form onSubmit={handleDeleteFolder}>
                    <div>
                        <label>Ім'я папки:</label>
                        <input type="text" name="deleteFolderName" value={deleteFolderName} onChange={handleChange} required />
                    </div>
                    <button type="submit">Видалити папку</button>
                </form>
            </div>
        </div>
    );
}

export default UserFileManager;
