import React, { useState } from 'react';
import Backendless from 'backendless';

function UserFileManager() {
    const [folderName, setFolderName] = useState('');
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFolderName(e.target.value);
    };

    const handleCreateFolder = async (e) => {
        e.preventDefault();

        const user = Backendless.UserService.currentUser;

        try {
            // const userDirectory = /user_files/${user.name};
            await Backendless.Files.createDirectory(`/user_files/${user.name}/${folderName}`);
            alert(`Папка ${folderName} успішно створена!`);
        } catch (error) {
            console.error('Error creating folder:', error);
            setError('Помилка під час створення папки');
        }
    };

    const handleDeleteFolder = async (e) => {
        e.preventDefault();
        const user = Backendless.UserService.currentUser;

        try {
            // const userDirectory = /user_files/${Backendless.UserService.getCurrentUser().name};
            await Backendless.Files.removeDirectory(`/user_files/${user.name}/${folderName}`);
            alert(`Папка ${folderName} успішно видалена!`);
        } catch (error) {
            console.error('Error deleting folder:', error);
            setError('Помилка під час видалення папки');
        }
    };

    return (
        <div>
            <div>
                <h2>Створення папки</h2>
                {error && <p style={{color: 'red'}}>{error}</p>}
                <form onSubmit={this.handleCreateFolder}>
                    <div>
                        <label>Ім'я папки:</label>
                        <input type="text" name="createFolderName" value={error} onChange={this.handleChange}
                               required/>
                    </div>
                    <button type="submit">Створити папку</button>
                </form>
            </div>

            <div>
                <h2>Видалення папки</h2>
                {error && <p style={{color: 'red'}}>{error}</p>}
                <form onSubmit={this.handleDeleteFolder}>
                    <div>
                        <label>Ім'я папки:</label>
                        <input type="text" name="deleteFolderName" value={folderName} onChange={this.handleChange}
                               required/>
                    </div>
                    <button type="submit">Видалити папку</button>
                </form>
            </div>
        </div>
    );
}

export default UserFileManager;
