import {Component} from "react";

class RegistrationForm extends Component {
    state = {
        email: '',
        password: '',
        name: '',
        age: '',
        gender: '',
        country: '',
        error: ''
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = async (e) => {
        e.preventDefault();

        // Виконати валідацію даних перед відправленням їх на сервер
        if (this.state.password.length < 6) {
            this.setState({ error: 'Пароль повинен містити принаймні 6 символів' });
            return;
        }

        // Виконати додаткову валідацію для віку
        const age = parseInt(this.state.age);
        if (age < 5) {
            this.setState({ error: 'Ви повинні бути старше 5 років для реєстрації' });
            return;
        }

        // Виконати валідацію електронної пошти
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(this.state.email)) {
            this.setState({ error: 'Введіть коректну адресу електронної пошти' });
            return;
        }

        // Створити об'єкт з даними користувача
        const user = {
            email: this.state.email,
            password: this.state.password,
            name: this.state.name,
            age: parseInt(this.state.age),
            gender: this.state.gender,
            country: this.state.country
        };

        try {

            // const work_dir = `/user_files/${user.name}`;
            // await Backendless.Files.createDirectory(work_dir)
            // const shared_dir = `/user_files/${user.name}/shared_with_me`;
            // await Backendless.Files.createDirectory(shared_dir)
            const registeredUser = await Backendless.UserService.register(user);

            // Успішно зареєстрований користувач
            console.log('Successfully registered user:', registeredUser);

            // Очистити форму після успішної реєстрації
            this.setState({
                email: '',
                password: '',
                name: '',
                age: '',
                gender: '',
                country: '',
                error: ''
            });

            // Додаткові дії після успішної реєстрації
            // Наприклад, перенаправлення на іншу сторінку
            // this.props.history.push('/dashboard');
        } catch (error) {
            // Помилка під час реєстрації користувача
            console.error('Error registering user:', error);
            this.setState({ error: 'Помилка при реєстрації користувача' });
        }
    };



    render() {
        const { email, password, name, age, gender, country, error } = this.state;

        return (
            <div>
                <h2>Реєстрація нового користувача</h2>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label>Email:</label>
                        <input type="email" name="email" value={email} onChange={this.handleChange} required />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input type="password" name="password" value={password} onChange={this.handleChange} required />
                    </div>
                    <div>
                        <label>Ім'я:</label>
                        <input type="text" name="name" value={name} onChange={this.handleChange} required />
                    </div>
                    <div>
                        <label>Вік:</label>
                        <input type="number" name="age" value={age} onChange={this.handleChange} required />
                    </div>
                    <div>
                        <label>Стать:</label>
                        <select name="gender" value={gender} onChange={this.handleChange} required>
                            <option value="">Виберіть стать</option>
                            <option value="male">Чоловіча</option>
                            <option value="female">Жіноча</option>
                            <option value="other">Інше</option>
                        </select>
                    </div>
                    <div>
                        <label>Країна:</label>
                        <input type="text" name="country" value={country} onChange={this.handleChange} required />
                    </div>
                    <button type="submit">Зареєструватися</button>
                </form>
            </div>
        );
    }
}

export default RegistrationForm;
