import React, { Component } from "react";
import Backendless from "backendless";

class LoginForm extends Component {
    state = {
        name: "",
        password: "",
        error: ""
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = async (e) => {
        e.preventDefault();

        const { name, password } = this.state;

        try {
            // Авторизувати користувача за допомогою Backendless
            const user = await Backendless.UserService.login(name, password, true);

            // Успішна авторизація
            console.log("Успішна авторизація:", user);

            // Очистити форму
            this.setState({
                name: "",
                password: "",
                error: ""
            });

            // Виконати додаткові дії після успішної авторизації
            // Наприклад, перенаправлення на іншу сторінку
            // this.props.history.push("/dashboard");
        } catch (error) {
            // Помилка під час авторизації
            console.error("Помилка авторизації:", error.message);
            this.setState({ error: error.message });
        }
    };

    render() {
        const { name, password, error } = this.state;

        return (
            <div>
                <h2>Авторизація</h2>
                {error && <p style={{ color: "red" }}>{error}</p>}
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label>Name:</label>
                        <input
                            type="text"
                            name="name"
                            value={name}
                            onChange={this.handleChange}
                            required
                        />
                    </div>
                    <div>
                        <label>Password:</label>
                        <input
                            type="password"
                            name="password"
                            value={password}
                            onChange={this.handleChange}
                            required
                        />
                    </div>
                    <button type="submit">Увійти</button>
                </form>
            </div>
        );
    }
}

export default LoginForm;
