import React, {useEffect, useState} from 'react';
import Backendless from 'backendless';
import {useNavigate} from "react-router";
import axios from 'axios';

const FileManager = () => {
    const [filesList, setFilesList] = useState([]);
    const [directoriesList, setDirectoriesList] = useState([]);
    const [currentDirectory, setCurrentDirectory] = useState('');
    const [newFolderName, setNewFolderName] = useState('');
    const [recipientName, setRecipientName] = useState('');
    const [isShareModalOpen, setIsShareModalOpen] = useState(false);
    const [fileToShare, setFileToShare] = useState('');
    const user = Backendless.UserService.currentUser;
    const navigate = useNavigate();

    useEffect(() => {
        fetchFilesList(currentDirectory);
    }, [currentDirectory]);

    const handleCreateFolder = async () => {
        try {
            const path = `/user_files/${user.name}/${currentDirectory}/${newFolderName}`;
            await Backendless.Files.createDirectory(path)
            fetchFilesList(currentDirectory);
        } catch (error) {
            console.error('Failed to create folder:', error);
        }
    };

    const fetchFilesList = async (dir) => {
        try {
            const path = '/user_files/' + user.name + '/' + dir;
            const fileList = await Backendless.Files.listing(path);
            const files = fileList.filter(file => file.name.indexOf('.') !== -1);
            const directories = fileList.filter(file => file.name.indexOf('.') === -1);
            setFilesList(files);
            setDirectoriesList(directories);
        } catch (error) {
            console.error('Failed to fetch files:', error);
        }
    };

    const handleDelete = async (fileName) => {
        try {
            const filePath = `/user_files/${user.name}/${currentDirectory}/${fileName}`;
            await Backendless.Files.remove(filePath);
            await fetchFilesList(currentDirectory);
        } catch (error) {
            console.error('Failed to delete file:', error);
        }
    };

    const handleUploadFile = async (event) => {
        try {
            await Backendless.Files.upload(event.target.files[0], `/user_files/${user.name}/${currentDirectory}/${file.name}`);
            fetchFilesList(currentDirectory);
        } catch (error) {
            console.error('Failed to upload file:', error);
        }
    };



    const handleShareFile = async () => {
        try {
            const whereClause = "name = '" + recipientName + "'";
            const queryBuilder = Backendless.DataQueryBuilder.create().setWhereClause(whereClause);
            const userExists = await Backendless.Data.of("Users").find(queryBuilder);
            if (userExists.length === 0) {
                alert('Користувача не існує');
                return;
            }
            const pathToSave = `/user_files/${recipientName}/shared_with_me`;
            const downloadLink = fileToShare.publicUrl;
            await Backendless.Files.saveFile(pathToSave, fileToShare.name + '.txt', downloadLink, true);
            alert('Файл успішно передано');
        } catch (error) {
            console.error('Failed to share file:', error);
        } finally {
            handleShareModal();
        }
    };

    const handleDownloadFile = async (file) => {
        if (currentDirectory.includes('shared_with_me')) {
            const response = await axios.get(file.publicUrl);
            const link = response.data;
            window.open(link, "_blank");
        } else {
            window.open(file.publicUrl, "_blank");
        }
    }

    const handleExitClick = () => {
        Backendless.UserService.logout().then(() => navigate('/'))
    };
    const handleDirectoryNavigation = (dirName) => {
        if (dirName === "..") {
            const dirs = currentDirectory.split('/');
            dirs.pop();
            setCurrentDirectory(dirs.join('/'));
        } else {
            setCurrentDirectory(currentDirectory ? `${currentDirectory}/${dirName}` : dirName);
        }
    };

    const handleShareModal = (file, isOpen) => {
        setIsShareModalOpen(isOpen);
        if (!isOpen) {
            setRecipientName('');
            setFileToShare('');
        } else {
            setFileToShare(file);
        }
    };

    return (
        <div>
            <h2>Управління файлами ({user?.name})</h2>
            {isShareModalOpen && (
                <div>
                    <input type="text" value={recipientName} onChange={e => setRecipientName(e.target.value)}
                           placeholder="Ім'я користувача" />
                    <button onClick={handleShareFile}>Поділитися</button>
                    <button onClick={handleShareModal}>Закрити</button>
                </div>
            )}
            <button onClick={handleExitClick}>Вийти з системи</button>
            {currentDirectory && <button onClick={handleDirectoryNavigation}>Назад</button>}
            <input type="text" value={newFolderName} onChange={e => setNewFolderName(e.target.value)}
                   placeholder="Ім'я нової папки" />
            <button onClick={handleCreateFolder}>Створити папку</button>
            <input type="file" onChange={handleUploadFile} />
            <ul>
                {directoriesList.map(dir => (
                    <li key={dir.name}>
                        <span>{dir.name}</span>
                        {dir.name !== 'shared_with_me' &&
                            <button onClick={() => handleDelete(dir.name)}>Видалити</button>}
                        <button onClick={() => handleDirectoryNavigation(dir.name)}>Відкрити</button>
                    </li>
                ))}
                {filesList.map(file => (
                    <li key={file.name}>
                        <span>{file.name}</span>
                        <button onClick={() => handleDelete(file.name)}>Видалити</button>
                        {currentDirectory !== 'shared_with_me' && <button onClick={() => handleShareModal(file)}>Поділитися</button>}
                        <button onClick={() => handleDownloadFile(file)}>Завантажити</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FileManager;
