import React, {useState, useEffect} from 'react';
import Backendless from 'backendless';
import {useNavigate} from 'react-router-dom';

const FileExplorer = () => {
    const [fileList, setFileList] = useState([]);
    const [directoryList, setDirectoryList] = useState([]);
    const [currentDirectory, setCurrentDirectory] = useState('');
    const [newDirectoryName, setNewDirectoryName] = useState('');
    const [sharedWithUsername, setSharedWithUsername] = useState('');
    const [isShareDialogOpen, setIsShareDialogOpen] = useState(false);
    const [fileToShare, setFileToShare] = useState(null);
    const user = Backendless.UserService.currentUser;
    const navigate = useNavigate();

    useEffect(() => {
        fetchDirectoryContents(currentDirectory);
    }, [currentDirectory]);

    const fetchDirectoryContents = async (dir) => {
        try {
            const path = '/user_files/' + user.name + '/' + dir;
            const filesAndDirs = await Backendless.Files.listing(path);
            const directories = filesAndDirs.filter(item => item.name.indexOf('.') === -1);
            const files = filesAndDirs.filter(item => item.name.indexOf('.') !== -1);
            setFileList(files);
            setDirectoryList(directories);
        } catch (error) {
            console.error('Failed to fetch files:', error);
        }
    };

    const handleCreateDirectory = async () => {
        try {
            await Backendless.Files.createDirectory(`/user_files/${user.name}/${currentDirectory}/${newDirectoryName}`);
            fetchDirectoryContents(currentDirectory);
        } catch (error) {
            console.error('Failed to create folder:', error);
        }
    };
    const handleDelete = async (itemName) => {
        try {
            await Backendless.Files.remove(`/user_files/${user.name}/${currentDirectory}/${itemName}`);
            fetchDirectoryContents(currentDirectory);
        } catch (error) {
            console.error('Failed to delete item:', error);
        }
    };

    const handleUploadFile = async (event) => {
        const file = event.target.files[0];
        const path = `/user_files/${user.name}/${currentDirectory}/${file.name}`;
        try {
            await Backendless.Files.upload(file, path);
            fetchDirectoryContents(currentDirectory);
        } catch (error) {
            console.error('Failed to upload file:', error);
        }
    };

    const handleDirectoryNavigation = (dirName) => {
        if (dirName === "..") {
            const dirs = currentDirectory.split('/');
            dirs.pop();
            setCurrentDirectory(dirs.join('/'));
        } else {
            setCurrentDirectory(currentDirectory ? `${currentDirectory}/${dirName}` : dirName);
        }
    };

    const handleShareDialogToggle = (fileName = null) => {
        if (fileName) {
            setFileToShare(fileName);
        }
        setIsShareDialogOpen(!isShareDialogOpen);
        setSharedWithUsername('');
    };


    const checkUserExists = async () => {
        try {
            const userExists = await Backendless.Data.of(Backendless.User).find({condition: `name = '${sharedWithUsername}'`});
            return userExists.length > 0;
        } catch (error) {
            console.error('Failed to check user existence:', error);
            return false;
        }
    };

    const saveSharedFile = async () => {
        try {
            const filePath = `/user_files/${sharedWithUsername}/shared_with_me/${fileToShare}.txt`;
            const fileLink = `/user_files/${user.name}/${currentDirectory}/${fileToShare}`;
            await Backendless.Files.saveFile(filePath, fileLink, true);
            alert('Файл успішно поділено');
        } catch (error) {
            console.error('Failed to save shared file:', error);
        }
    };


    const handleShareFile = async () => {
        try {
            const whereClause = "name = '" + shareName + "'";
            const queryBuilder = Backendless.DataQueryBuilder.create().setWhereClause(whereClause);
            const userExists = await Backendless.Data.of("Users").find(queryBuilder);
            if (userExists.length === 0) {
                alert('Користувача не існує');
                return;
            }
            const pathToSave = `/user_files/${shareName}/shared_with_me`;
            console.log(pathToSave);
            const downloadLink = fileToShare.publicUrl;
            console.log(downloadLink);
            await Backendless.Files.saveFile(pathToSave, fileToShare.name + '.txt', downloadLink, true);
            alert('Файл успішно передано');
        } catch (error) {
            console.error('Failed to share file:', error);
        } finally {
            handleCloseShareModal();
        }
    };


    const handleCloseShareDialog = () => {
        setIsShareDialogOpen(false);
        setSharedWithUsername('');
        setFileToShare(null);
    };

    const handleSharedFileDownload = async (fileName) => {
        try {
            // Отримуємо шлях до текстового файлу з посиланням на завантаження
            const filePath = `/user_files/${user.name}/shared_with_me/${fileName}.txt`;
            // Завантажуємо вміст текстового файлу
            const fileContent = await Backendless.Files.loadFile(filePath);
            // Витягуємо посилання з вмісту текстового файлу
            const fileLink = fileContent.trim(); // Припускаємо, що текстовий файл містить лише одне посилання без зайвих пробілів
            // Переходимо за посиланням для завантаження файлу
            window.open(fileLink);
        } catch (error) {
            console.error('Failed to download shared file:', error);
        }
    };

    const handleLogout = () => {
        Backendless.UserService.logout().then(r => navigate('/'))
    };

    const handleRefresh = () => {
        fetchDirectoryContents(currentDirectory);
    };

    return (
        <div>
            <h2>Файловий менеджер ({user?.name})</h2>
            <button onClick={handleRefresh}>Оновити сторінку</button>
            {isShareDialogOpen && (
                <div>
                    <input type="text" value={sharedWithUsername} onChange={e => setSharedWithUsername(e.target.value)}
                           placeholder="Ім'я користувача"/>
                    <button onClick={() => handleShareFile(fileToShare)}>Поділитися</button>
                    <button onClick={handleShareDialogToggle}>Закрити</button>
                </div>
            )}
            <button onClick={() => handleLogout()}>Вийти з системи</button>
            {currentDirectory && <button onClick={() => handleDirectoryNavigation("..")}>Назад</button>}
            <input type="text" value={newDirectoryName} onChange={e => setNewDirectoryName(e.target.value)}
                   placeholder="Ім'я нової папки"/>
            <button onClick={handleCreateDirectory}>Створити папку</button>
            <input type="file" onChange={handleUploadFile}/>
            <ul>
                {directoryList.map(dir => (
                    <li key={dir.name}>
                        <span>{dir.name}</span>
                        {dir.name !== 'shared_with_me' &&
                            <button onClick={() => handleDelete(dir.name)}>Видалити</button>}
                        <button onClick={() => handleDirectoryNavigation(dir.name)}>Відкрити</button>
                    </li>
                ))}
                {fileList.map(file => (
                    <li key={file.name}>
                        <span>{file.name}</span>
                        <button onClick={() => handleDelete(file.name)}>Видалити</button>
                        <button onClick={() => handleShareDialogToggle(file.name)}>Поділитися</button>
                        <a href={file.publicUrl} download>Скачати</a>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default FileExplorer;
