import React, { Component } from 'react';
import Backendless from 'backendless';

class ForgotPasswordForm extends Component {
    state = {
        email: '',
        error: null,
        success: false
    };

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    handleSubmit = async (e) => {
        e.preventDefault();

        const { email } = this.state;

        try {
            // Викликаємо метод Backendless для відновлення пароля
            await Backendless.UserService.restorePassword(email);

            // Відправлено лист з інструкціями по відновленню пароля
            this.setState({ success: true, error: null });
        } catch (error) {
            // Помилка під час відновлення пароля
            console.error('Error restoring password:', error.message);
            this.setState({ error: 'Помилка при відновленні пароля', success: false });
        }
    };

    render() {
        const { email, error, success } = this.state;

        return (
            <div>
                <h2>Відновлення пароля</h2>
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {success && <p style={{ color: 'green' }}>Лист з інструкціями по відновленню пароля надіслано на вашу пошту</p>}
                <form onSubmit={this.handleSubmit}>
                    <div>
                        <label>Email:</label>
                        <input type="email" name="email" value={email} onChange={this.handleChange} required />
                    </div>
                    <button type="submit">Відновити пароль</button>
                </form>
            </div>
        );
    }
}

export default ForgotPasswordForm;
