import React, { useState } from "react";
import Backendless from "backendless";
import { useNavigate } from "react-router-dom";
import ForgotPassword from "./ForgotPasswordForm";
import './LoginForm.css'
const LoginForm = () => {
    const [name, setName] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
    const [showForgotPasswordForm, setShowForgotPasswordForm] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === "name") {
            setName(value);
        } else if (name === "password") {
            setPassword(value);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const user = await Backendless.UserService.login(name, password, true);
            console.log("Успішна авторизація:", user);

            setName("");
            setPassword("");
            setError("");
            navigate("/fileManager");

        } catch (error) {
            console.error("Помилка авторизації:", error.message);
            setError(error.message);
        }
    };

    const handleResetPassword = () => {
        setShowForgotPasswordForm(true);
    };

    return (
        <div>
            <h2>Авторизація</h2>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={name}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        name="password"
                        value={password}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <button type="submit">Увійти</button>
                    <button type="button" onClick={handleResetPassword}>Скинути пароль</button>
                </div>
            </form>
            {showForgotPasswordForm && <ForgotPassword />}
        </div>
    );
};

export default LoginForm;
